import React, { useEffect, useState, useRef } from "react";
import Layout from "./Layout";

const UserDropdown = ({ userList, selectedUser, setSelectedUser }) => {
  const [open, setOpen] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    function handleClickOutside(e) {
      if (ref.current && !ref.current.contains(e.target)) setOpen(false);
    }
    if (open) document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [open]);

  return (
    <div className="relative" ref={ref}>
      <button
        className="flex items-center gap-2 px-4 py-2 bg-gray-800 border border-gray-700 rounded-md text-gray-100 font-poppins text-lg shadow hover:bg-gray-700 transition focus:outline-none focus:ring-2 focus:ring-blue-500"
        onClick={() => setOpen((v) => !v)}
        aria-haspopup="listbox"
        aria-expanded={open}
      >
        <svg className="w-6 h-6 text-blue-400" fill="currentColor" viewBox="0 0 20 20"><path d="M10 10a4 4 0 100-8 4 4 0 000 8zm-7 8a7 7 0 1114 0H3z" /></svg>
        <span>{selectedUser}</span>
        <svg className="w-5 h-5 text-gray-400 ml-1" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" /></svg>
      </button>
      {open && (
        <div className="absolute left-0 mt-2 w-48 bg-gray-900 border border-gray-700 rounded-md shadow-lg z-50 animate-fade-in flex flex-col py-2">
          {userList.map((u) => (
            <button
              key={u}
              className={`flex items-center gap-2 px-4 py-2 rounded-md hover:bg-gray-800 transition text-left text-gray-100 font-poppins ${selectedUser === u ? 'bg-gray-800' : ''}`}
              onClick={() => {
                setSelectedUser(u);
                setOpen(false);
              }}
            >
              <svg className="w-5 h-5 text-blue-400" fill="currentColor" viewBox="0 0 20 20"><path d="M10 10a4 4 0 100-8 4 4 0 000 8zm-7 8a7 7 0 1114 0H3z" /></svg>
              <span>{u}</span>
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

const TaskDashborad = ({ username, isAdmin }) => {
  const [tasks, setTasks] = useState([]);
  const [textInput, setTextInput] = useState("");
  const [selectedPriority, setSelectedPriority] = useState("High");
  const [selectedTask, setSelectedTask] = useState(null);
  const [priorityDropdownOpen, setPriorityDropdownOpen] = useState(false);
  const priorityDropdownRef = useRef(null);
  const [selectedUser, setSelectedUser] = useState(username);
  const [userList, setUserList] = useState([]);

  // Always enable dark mode
  useEffect(() => {
    document.documentElement.classList.add("dark");
  }, []);

  // Load user list for admin
  useEffect(() => {
    if (isAdmin) {
      const users = Object.keys(JSON.parse(localStorage.getItem("users") || "{}"));
      setUserList(users.filter(u => u !== "admin"));
      setSelectedUser(users.filter(u => u !== "admin")[0] || "");
    } else {
      setSelectedUser(username);
    }
  }, [isAdmin, username]);

  // UseEffect For StoredTasks in Local Stroage
  useEffect(() => {
    const storedTasks = localStorage.getItem(`tasks_${selectedUser}`);
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    } else {
      setTasks([]);
    }
  }, [selectedUser]);
  useEffect(() => {
    localStorage.setItem(`tasks_${selectedUser}`, JSON.stringify(tasks));
  }, [tasks, selectedUser]);

  // Close dropdown on outside click
  useEffect(() => {
    function handleClickOutside(event) {
      if (priorityDropdownRef.current && !priorityDropdownRef.current.contains(event.target)) {
        setPriorityDropdownOpen(false);
      }
    }
    if (priorityDropdownOpen) {
      document.addEventListener("mousedown", handleClickOutside);
    } else {
      document.removeEventListener("mousedown", handleClickOutside);
    }
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [priorityDropdownOpen]);

  const handleTextInputChange = (event) => {
    setTextInput(event.target.value);
  };

  const handleTaskSubmit = () => {
    if (textInput.trim() === "") {
      return;
    }
    const newTask = {
      text: textInput,
      priority: selectedPriority,
    };
    setTasks([...tasks, newTask]);
    setTextInput("");
    setSelectedPriority("High");
  };

  const getTasksByPriority = (priority) => {
    return tasks.filter((task) => task.priority === priority);
  };

  const handleEditTask = (editedText) => {
    const updatedTasks = tasks.map((task) =>
      task === selectedTask ? { ...task, text: editedText } : task
    );
    setTasks(updatedTasks);
    setSelectedTask(null);
  };

  const handleChangePriority = (newPriority) => {
    const updatedTasks = tasks.map((task) =>
      task === selectedTask ? { ...task, priority: newPriority } : task
    );
    setTasks(updatedTasks);
    setSelectedTask(null);
  };

  const handleDeleteTask = () => {
    const updatedTasks = tasks.filter((task) => task !== selectedTask);
    setTasks(updatedTasks);
    setSelectedTask(null);
  };

  const priorities = [
    {
      value: "High",
      label: "High",
      icon: <span className="text-red-500 text-lg">🔥</span>,
      color: "text-red-500"
    },
    {
      value: "Medium",
      label: "Medium",
      icon: <span className="text-yellow-400 text-lg">⚡</span>,
      color: "text-yellow-400"
    },
    {
      value: "Low",
      label: "Low",
      icon: <span className="text-green-400 text-lg">🌱</span>,
      color: "text-green-400"
    }
  ];

  return (
    <div className="relative min-h-screen flex items-center justify-center py-8 overflow-hidden">
      {/* Floating Particles Background */}
      <div className="particles-container">
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
        <div className="particle"></div>
      </div>
      <div className="absolute inset-0 z-0 bg-gradient-to-br from-gray-900 to-gray-800 animate-gradient-move" />
      <div className="w-full max-w-6xl mx-auto z-10 font-inter">
        {isAdmin && (
          <div className="mb-6 flex items-center gap-4">
            <label className="text-gray-300 font-poppins text-lg">Manage Tasks for:</label>
            <UserDropdown userList={userList} selectedUser={selectedUser} setSelectedUser={setSelectedUser} />
          </div>
        )}
        <div className="lg:flex grid gap-4 items-center font-main bg-gray-900 rounded-md shadow-lg p-6 mb-8 border border-gray-700">
          <div className="w-full lg:w-1/2">
            <input
              type="text"
              value={textInput}
              onChange={handleTextInputChange}
              className="w-full border-2 border-gray-700 focus:border-blue-600 rounded-md p-3 transition outline-none text-lg bg-gray-800 text-gray-100 placeholder-gray-500 font-inter"
              placeholder="Enter a new task..."
            />
          </div>
          <div className="w-full lg:w-1/4" ref={priorityDropdownRef}>
            <button
              type="button"
              className="w-full border-2 border-gray-700 focus:border-blue-600 rounded-md p-3 transition outline-none text-lg bg-gray-800 text-gray-100 flex items-center justify-between gap-2"
              onClick={() => setPriorityDropdownOpen((v) => !v)}
              aria-haspopup="listbox"
              aria-expanded={priorityDropdownOpen}
            >
              <span className={`flex items-center gap-2 ${priorities.find(p => p.value === selectedPriority).color}`}>
                {priorities.find(p => p.value === selectedPriority).icon}
                {priorities.find(p => p.value === selectedPriority).label}
              </span>
              <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" /></svg>
            </button>
            {priorityDropdownOpen && (
              <div className="absolute mt-2 w-48 bg-gray-900 border border-gray-700 rounded-md shadow-lg z-50 animate-fade-in flex flex-col py-2">
                {priorities.map((p) => (
                  <button
                    key={p.value}
                    className={`flex items-center gap-2 px-4 py-2 rounded-md hover:bg-gray-800 transition text-left ${p.color} ${selectedPriority === p.value ? 'bg-gray-800' : ''}`}
                    onClick={() => {
                      setSelectedPriority(p.value);
                      setPriorityDropdownOpen(false);
                    }}
                  >
                    {p.icon}
                    <span className="font-medium">{p.label}</span>
                  </button>
                ))}
              </div>
            )}
          </div>
          <div className="w-full lg:w-1/4 flex justify-end">
            <button onClick={handleTaskSubmit} className="bg-blue-700 hover:bg-blue-800 text-white font-semibold px-6 py-3 rounded-md shadow transition">
              Add Task
            </button>
          </div>
        </div>

        <div className="mt-8 space-y-4 text-gray-100">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* High Priority */}
            <Layout
              getTasksByPriority={getTasksByPriority}
              setSelectedTask={setSelectedTask}
              selectedTask={selectedTask}
              handleEditTask={handleEditTask}
              handleChangePriority={handleChangePriority}
              handleDeleteTask={handleDeleteTask}
              level="High"
            />
            {/* Medium Priority */}
            <Layout
              getTasksByPriority={getTasksByPriority}
              setSelectedTask={setSelectedTask}
              selectedTask={selectedTask}
              handleEditTask={handleEditTask}
              handleChangePriority={handleChangePriority}
              handleDeleteTask={handleDeleteTask}
              level="Medium"
            />
            {/* Low Priority */}
            <Layout
              getTasksByPriority={getTasksByPriority}
              setSelectedTask={setSelectedTask}
              selectedTask={selectedTask}
              handleEditTask={handleEditTask}
              handleChangePriority={handleChangePriority}
              handleDeleteTask={handleDeleteTask}
              level="Low"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskDashborad;
