import React from "react";
import Action from "./Action";

function getPriorityStyles(level) {
  switch (level) {
    case "High":
      return {
        card: "bg-red-50 border-red-300",
        header: "text-red-700",
        accent: "bg-red-500"
      };
    case "Medium":
      return {
        card: "bg-yellow-50 border-yellow-300",
        header: "text-yellow-700",
        accent: "bg-yellow-500"
      };
    case "Low":
      return {
        card: "bg-green-50 border-green-300",
        header: "text-green-700",
        accent: "bg-green-500"
      };
    default:
      return {
        card: "bg-gray-50 border-gray-300",
        header: "text-gray-700",
        accent: "bg-gray-500"
      };
  }
}

function Layout(props) {
  const styles = getPriorityStyles(props.level);
  return (
    <div className={`rounded-2xl shadow-lg border-2 p-5 min-h-[350px] flex flex-col ${styles.card}`}> 
      <h2 className={`text-xl font-bold mb-4 flex items-center gap-2 ${styles.header}`}>
        <span className={`w-3 h-3 rounded-full ${styles.accent}`}></span>
        {props.level} Priority
      </h2>
      <div className="flex-1">
        {props.getTasksByPriority(props.level).length === 0 && (
          <p className="text-gray-400 italic text-center mt-10">No tasks</p>
        )}
        {props.getTasksByPriority(props.level).map((task, index) => (
          <div
            key={index}
            className="bg-white p-3 rounded-lg mb-3 shadow-sm flex items-center justify-between transition hover:shadow-md border border-gray-100 group"
          >
            <p
              className="text-base cursor-pointer font-secondary group-hover:text-blue-600 transition"
              onClick={() => props.setSelectedTask(task)}
            >
              â€¢ {task.text}
            </p>
            {props.selectedTask === task && (
              <Action
                priority={props.level}
                handleEditTask={props.handleEditTask}
                handleChangePriority={props.handleChangePriority}
                handleDeleteTask={props.handleDeleteTask}
                selectedTask={props.selectedTask}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Layout;
