import React from "react";

const iconBtn = (
  { onClick, label, icon, color, hoverColor, ringColor }
) => (
  <button
    onClick={onClick}
    className={`group relative flex items-center justify-center w-10 h-10 rounded-full bg-gray-800 text-gray-300 hover:${color} hover:bg-gray-700 transition transform hover:scale-110 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-${ringColor}`}
    tabIndex={0}
  >
    {icon}
    <span className="absolute bottom-[-2.2rem] left-1/2 -translate-x-1/2 px-2 py-1 text-xs rounded bg-gray-900 text-white opacity-0 group-hover:opacity-100 pointer-events-none transition whitespace-nowrap z-20">
      {label}
    </span>
  </button>
);

const Action = (props) => {
  return (
    <div className="flex gap-2">
      {iconBtn({
        onClick: () => {
          const edited = prompt("Edit task:", props.selectedTask.text);
          if (edited !== null && edited.trim() !== "") props.handleEditTask(edited);
        },
        label: "Edit",
        icon: (
          <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M15.232 5.232l3.536 3.536M9 13l6.586-6.586a2 2 0 112.828 2.828L11.828 15.828a2 2 0 01-2.828 0L9 13zm0 0V21h8" /></svg>
        ),
        color: "text-blue-400",
        hoverColor: "text-blue-500",
        ringColor: "blue-500"
      })}
      {iconBtn({
        onClick: () => {
          const newPriority = prompt("Enter new priority:", props.selectedTask.priority);
          if (newPriority) props.handleChangePriority(newPriority);
        },
        label: "Change Priority",
        icon: (
          <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M8 7V3m8 4V3m-9 4h10a2 2 0 012 2v10a2 2 0 01-2 2H7a2 2 0 01-2-2V9a2 2 0 012-2zm3 4h4m-2 0v4" /></svg>
        ),
        color: "text-yellow-400",
        hoverColor: "text-yellow-500",
        ringColor: "yellow-500"
      })}
      {iconBtn({
        onClick: () => {
          if (window.confirm(`Are you sure you want to delete this ${props.priority} priority task?`)) {
            props.handleDeleteTask();
          }
        },
        label: "Delete",
        icon: (
          <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" /></svg>
        ),
        color: "text-red-400",
        hoverColor: "text-red-500",
        ringColor: "red-500"
      })}
    </div>
  );
};

export default Action;
