import React, { useState } from "react";

const Auth = ({ onLogin }) => {
  const [isSignup, setIsSignup] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username === "admin" && password === "admin1234") {
      setError("");
      onLogin("admin", true);
      return;
    }
    const users = JSON.parse(localStorage.getItem("users") || "{}");
    if (isSignup) {
      if (!username || !password) {
        setError("Please enter a username and password.");
        return;
      }
      if (users[username]) {
        setError("Username already exists.");
        return;
      }
      users[username] = password;
      localStorage.setItem("users", JSON.stringify(users));
      setError("");
      onLogin(username, false);
    } else {
      if (users[username] && users[username] === password) {
        setError("");
        onLogin(username, false);
      } else {
        setError("Invalid username or password.");
      }
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-gray-900 to-gray-800">
      <form onSubmit={handleSubmit} className="bg-gray-900 p-8 rounded-xl shadow-lg w-96 border border-gray-700">
        <h2 className="text-2xl font-poppins text-center mb-6 text-gray-100">{isSignup ? "Sign Up" : "Login"}</h2>
        {error && <div className="mb-4 text-red-400 text-center">{error}</div>}
        <div className="mb-4">
          <input
            type="text"
            placeholder="Username"
            className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 text-gray-100 focus:outline-none focus:border-blue-500"
            value={username}
            onChange={e => setUsername(e.target.value)}
            autoFocus
          />
        </div>
        <div className="mb-6">
          <input
            type="password"
            placeholder="Password"
            className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 text-gray-100 focus:outline-none focus:border-blue-500"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-lg shadow transition mb-4">
          {isSignup ? "Sign Up" : "Login"}
        </button>
        <div className="text-center text-gray-400">
          {isSignup ? (
            <>
              Already have an account?{' '}
              <button type="button" className="text-blue-400 hover:underline" onClick={() => { setIsSignup(false); setError(""); }}>
                Login
              </button>
            </>
          ) : (
            <>
              Don't have an account?{' '}
              <button type="button" className="text-blue-400 hover:underline" onClick={() => { setIsSignup(true); setError(""); }}>
                Sign Up
              </button>
            </>
          )}
        </div>
      </form>
    </div>
  );
};

export default Auth;
