import React, { useEffect, useState } from "react";
import Layout from "./Layout";

const TaskDashborad = () => {
  const [tasks, setTasks] = useState([]);
  const [textInput, setTextInput] = useState("");
  const [selectedPriority, setSelectedPriority] = useState("High");
  const [selectedTask, setSelectedTask] = useState(null);
  const [theme, setTheme] = useState(() => localStorage.getItem("theme") || "light");

  // Apply theme to html element
  useEffect(() => {
    document.documentElement.classList.toggle("dark", theme === "dark");
    localStorage.setItem("theme", theme);
  }, [theme]);

  // UseEffect For StoredTasks in Local Stroage
  useEffect(() => {
    const storedTasks = localStorage.getItem("tasks");
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
  }, []);
  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  // With Help Of State Management We Handle Input Change
  const handleTextInputChange = (event) => {
    setTextInput(event.target.value);
  };
  // handlePriorityChange
  const handlePriorityChange = (event) => {
    setSelectedPriority(event.target.value);
  };

  // Handle Function When Click On Submit
  const handleTaskSubmit = () => {
    if (textInput.trim() === "") {
      return;
    }
    // Selecting The New Task To Put
    const newTask = {
      text: textInput,
      priority: selectedPriority,
    };

    setTasks([...tasks, newTask]);
    setTextInput("");
    setSelectedPriority("High");
  };

  const getTasksByPriority = (priority) => {
    return tasks.filter((task) => task.priority === priority);
  };

  const handleEditTask = (editedText) => {
    const updatedTasks = tasks.map((task) =>
      task === selectedTask ? { ...task, text: editedText } : task
    );
    setTasks(updatedTasks);
    setSelectedTask(null);
  };

  const handleChangePriority = (newPriority) => {
    const updatedTasks = tasks.map((task) =>
      task === selectedTask ? { ...task, priority: newPriority } : task
    );
    setTasks(updatedTasks);
    setSelectedTask(null);
  };

  const handleDeleteTask = () => {
    const updatedTasks = tasks.filter((task) => task !== selectedTask);
    setTasks(updatedTasks);
    setSelectedTask(null);
  };

  // Animated background placeholder (will add animation next)
  return (
    <div className="relative min-h-screen flex items-center justify-center py-8 overflow-hidden">
      {/* Moving background will go here */}
      <div className="absolute inset-0 z-0 bg-gradient-to-br from-blue-50 to-purple-100 dark:from-gray-900 dark:to-gray-800 animate-gradient-move" />
      {/* Theme toggle button */}
      <button
        className="absolute top-6 right-8 z-10 bg-white/80 dark:bg-gray-800/80 border border-gray-300 dark:border-gray-700 rounded-full px-4 py-2 shadow hover:scale-105 transition text-gray-700 dark:text-gray-200 font-semibold"
        onClick={() => setTheme(theme === "dark" ? "light" : "dark")}
        aria-label="Toggle dark mode"
      >
        {theme === "dark" ? "üåô Dark" : "‚òÄÔ∏è Light"}
      </button>
      <div className="w-full max-w-6xl mx-auto z-10">
        <div className="lg:flex grid gap-4 items-center font-main bg-white/80 dark:bg-gray-900/80 rounded-xl shadow-lg p-6 mb-8 border border-gray-200 dark:border-gray-700">
          <div className="w-full lg:w-1/2">
            <input
              type="text"
              value={textInput}
              onChange={handleTextInputChange}
              className="w-full border-2 border-gray-300 dark:border-gray-700 focus:border-blue-400 dark:focus:border-blue-600 rounded-lg p-3 transition outline-none text-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 placeholder-gray-400 dark:placeholder-gray-500"
              placeholder="Enter a new task..."
            />
          </div>
          <div className="w-full lg:w-1/4">
            <select
              value={selectedPriority}
              onChange={handlePriorityChange}
              className="w-full border-2 border-gray-300 dark:border-gray-700 focus:border-blue-400 dark:focus:border-blue-600 rounded-lg p-3 transition outline-none text-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100"
            >
              <option value="High">High Priority</option>
              <option value="Medium">Medium Priority</option>
              <option value="Low">Low Priority</option>
            </select>
          </div>
          <div className="w-full lg:w-1/4 flex justify-end">
            <button onClick={handleTaskSubmit} className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-6 py-3 rounded-lg shadow transition">
              Add Task
            </button>
          </div>
        </div>

        <div className="mt-8 space-y-4 text-black dark:text-gray-100">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* High Priority */}
            <Layout
              getTasksByPriority={getTasksByPriority}
              setSelectedTask={setSelectedTask}
              selectedTask={selectedTask}
              handleEditTask={handleEditTask}
              handleChangePriority={handleChangePriority}
              handleDeleteTask={handleDeleteTask}
              level="High"
            />
            {/* Medium Priority */}
            <Layout
              getTasksByPriority={getTasksByPriority}
              setSelectedTask={setSelectedTask}
              selectedTask={selectedTask}
              handleEditTask={handleEditTask}
              handleChangePriority={handleChangePriority}
              handleDeleteTask={handleDeleteTask}
              level="Medium"
            />
            {/* Low Priority */}
            <Layout
              getTasksByPriority={getTasksByPriority}
              setSelectedTask={setSelectedTask}
              selectedTask={selectedTask}
              handleEditTask={handleEditTask}
              handleChangePriority={handleChangePriority}
              handleDeleteTask={handleDeleteTask}
              level="Low"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default TaskDashborad;
