import React from "react";
import { Link, useLocation } from "react-router-dom";

const Navbar = ({ user, isAdmin, handleLogout }) => {
  const location = useLocation();
  return (
    <nav className="flex items-center justify-between px-8 py-6 bg-white/80 shadow-sm rounded-b-2xl border-b border-indigo-100 sticky top-0 z-50">
      <div className="flex items-center gap-2 select-none">
        <div className="w-8 h-8 rounded-lg bg-pink-400 mr-2" />
        <span className="text-2xl font-bold text-indigo-700 font-poppins">Logo</span>
      </div>
      <div className="flex items-center gap-8">
        <Link to="/" className="text-indigo-700 font-medium hover:text-indigo-900 transition">Home</Link>
        <a href="/#about" className="text-indigo-700 font-medium hover:text-indigo-900 transition">About</a>
        <a href="/#services" className="text-indigo-700 font-medium hover:text-indigo-900 transition">Services</a>
        <a href="/#contact" className="text-indigo-700 font-medium hover:text-indigo-900 transition">Contact</a>
      </div>
      <div className="flex items-center gap-4">
        {user ? (
          <>
            <span className="text-indigo-700 font-poppins text-lg flex items-center gap-1">
              <svg className="w-5 h-5 text-indigo-400" fill="currentColor" viewBox="0 0 20 20"><path d="M10 10a4 4 0 100-8 4 4 0 000 8zm-7 8a7 7 0 1114 0H3z" /></svg>
              {user}
              {isAdmin && <span className="ml-2 px-2 py-0.5 rounded bg-emerald-700 text-emerald-100 text-xs font-bold">Admin</span>}
            </span>
            <button
              className="bg-pink-400 hover:bg-pink-500 text-white px-5 py-2 rounded font-semibold transition shadow focus:outline-none focus:ring-2 focus:ring-pink-200"
              onClick={handleLogout}
            >
              Logout
            </button>
          </>
        ) : (
          <Link to="/login" className="bg-pink-400 hover:bg-pink-500 text-white px-6 py-2 rounded-full font-semibold shadow transition">
            Sign Up
          </Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
