import React from "react";

const Action = (props) => {
  return (
    <div className="mt-2 flex gap-2">
      <button
        className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg shadow transition focus:outline-none focus:ring-2 focus:ring-blue-300 dark:bg-blue-700 dark:hover:bg-blue-800 dark:focus:ring-blue-900"
        onClick={() => {
          const edited = prompt("Edit task:", props.selectedTask.text);
          if (edited !== null && edited.trim() !== "") props.handleEditTask(edited);
        }}
      >
        Edit
      </button>
      <button
        className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded-lg shadow transition focus:outline-none focus:ring-2 focus:ring-yellow-300 dark:bg-yellow-700 dark:hover:bg-yellow-800 dark:focus:ring-yellow-900"
        onClick={() => {
          const newPriority = prompt("Enter new priority:", props.selectedTask.priority);
          if (newPriority) props.handleChangePriority(newPriority);
        }}
      >
        Change Priority
      </button>
      <button
        className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg shadow transition focus:outline-none focus:ring-2 focus:ring-red-300 dark:bg-red-700 dark:hover:bg-red-800 dark:focus:ring-red-900"
        onClick={() => {
          if (window.confirm(`Are you sure you want to delete this ${props.priority} priority task?`)) {
            props.handleDeleteTask();
          }
        }}
      >
        Delete
      </button>
    </div>
  );
};

export default Action;
