import React, { useState, useEffect } from "react";
import "./App.css";
import TaskDashborad from "./Context/TaskDashborad";
import Auth from "./Context/Auth";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LandingPage from "./Context/LandingPage";

const App = () => {
  const [user, setUser] = useState(() => localStorage.getItem("currentUser") || "");
  const [isAdmin, setIsAdmin] = useState(() => localStorage.getItem("isAdmin") === "true");

  useEffect(() => {
    if (user) {
      localStorage.setItem("currentUser", user);
      localStorage.setItem("isAdmin", isAdmin);
    } else {
      localStorage.removeItem("currentUser");
      localStorage.removeItem("isAdmin");
    }
  }, [user, isAdmin]);

  const handleLogin = (username, adminFlag) => {
    setUser(username);
    setIsAdmin(adminFlag);
  };

  const handleLogout = () => {
    setUser("");
    setIsAdmin(false);
  };

  return (
    <Router basename="/react-task-management">
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/login" element={<Auth onLogin={handleLogin} />} />
        <Route path="/dashboard" element={user ? (
          <div>
            {/* Modern Navbar */}
            <nav className="backdrop-blur bg-slate-800/90 border-b border-slate-700 shadow-lg sticky top-0 z-50">
              <div className="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <span className="text-2xl font-poppins text-indigo-400 flex items-center gap-2 select-none">
                    <svg className="w-7 h-7 inline-block text-indigo-500" fill="none" stroke="currentColor" strokeWidth="2.2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M9 17v-2a2 2 0 012-2h2a2 2 0 012 2v2m-6 4h6a2 2 0 002-2v-6a2 2 0 00-2-2h-2V7a2 2 0 10-4 0v2H7a2 2 0 00-2 2v6a2 2 0 002 2z" /></svg>
                    TaskFlow
                  </span>
                </div>
                <div className="flex items-center gap-4">
                  <span className="text-slate-200 font-poppins text-lg flex items-center gap-1">
                    <svg className="w-5 h-5 text-indigo-400" fill="currentColor" viewBox="0 0 20 20"><path d="M10 10a4 4 0 100-8 4 4 0 000 8zm-7 8a7 7 0 1114 0H3z" /></svg>
                    {user}
                    {isAdmin && <span className="ml-2 px-2 py-0.5 rounded bg-emerald-700 text-emerald-100 text-xs font-bold">Admin</span>}
                  </span>
                  <button
                    className="bg-rose-600 hover:bg-rose-700 text-white px-5 py-2 rounded font-semibold transition shadow focus:outline-none focus:ring-2 focus:ring-rose-300"
                    onClick={handleLogout}
                  >
                    Logout
                  </button>
                </div>
              </div>
            </nav>
            <TaskDashborad username={user} isAdmin={isAdmin} />
          </div>
        ) : <Auth onLogin={handleLogin} />} />
      </Routes>
    </Router>
  );
};

export default App;
