import React from "react";
import Action from "./Action";

function getPriorityStyles(level, dark) {
  if (dark) {
    switch (level) {
      case "High":
        return {
          card: "bg-red-900/40 border-red-700",
          header: "text-red-300",
          accent: "bg-red-500"
        };
      case "Medium":
        return {
          card: "bg-yellow-900/40 border-yellow-700",
          header: "text-yellow-300",
          accent: "bg-yellow-400"
        };
      case "Low":
        return {
          card: "bg-green-900/40 border-green-700",
          header: "text-green-300",
          accent: "bg-green-400"
        };
      default:
        return {
          card: "bg-gray-900/40 border-gray-700",
          header: "text-gray-300",
          accent: "bg-gray-500"
        };
    }
  } else {
    switch (level) {
      case "High":
        return {
          card: "bg-red-50 border-red-300",
          header: "text-red-700",
          accent: "bg-red-500"
        };
      case "Medium":
        return {
          card: "bg-yellow-50 border-yellow-300",
          header: "text-yellow-700",
          accent: "bg-yellow-500"
        };
      case "Low":
        return {
          card: "bg-green-50 border-green-300",
          header: "text-green-700",
          accent: "bg-green-500"
        };
      default:
        return {
          card: "bg-gray-50 border-gray-300",
          header: "text-gray-700",
          accent: "bg-gray-500"
        };
    }
  }
}

function Layout(props) {
  const isDark = document.documentElement.classList.contains('dark');
  const styles = getPriorityStyles(props.level, isDark);
  return (
    <div className={`rounded-md shadow-lg border-2 p-5 min-h-[350px] flex flex-col ${styles.card} dark:${styles.card}`}> 
      <h2 className={`text-xl font-bold mb-4 flex items-center gap-2 ${styles.header} dark:${styles.header} font-poppins`}>
        <span className={`w-3 h-3 rounded-full ${styles.accent}`}></span>
        {props.level} Priority
      </h2>
      <div className="flex-1">
        {props.getTasksByPriority(props.level).length === 0 && (
          <div className="flex flex-col items-center justify-center mt-10 text-gray-400 dark:text-gray-500">
            <svg className="w-12 h-12 mb-3 opacity-50" fill="none" stroke="currentColor" strokeWidth="1.5" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
            </svg>
            <p className="font-poppins text-sm font-medium">No tasks yet</p>
            <p className="text-xs opacity-70 mt-1">Add a task to get started</p>
          </div>
        )}
        {props.getTasksByPriority(props.level).map((task, index) => (
          <div
            key={index}
            data-task-id={task.id}
            className={`${task.completed ? '' : 'gradient-hover'} shine-effect p-3 rounded-md mb-3 shadow-sm flex items-center justify-between border border-gray-700 group relative z-20 ${
              task.completed ? 'task-complete' : ''
            }`}
          >
            <p
              className="text-base cursor-pointer font-poppins group-hover:font-bold group-hover:text-white transition text-gray-100"
              onClick={() => props.setSelectedTask(task)}
            >
              â€¢ {task.text}
            </p>
            <div className="absolute right-3 top-1/2 -translate-y-1/2 opacity-0 group-hover:opacity-100 transition-opacity flex gap-2 z-50">
              <Action
                priority={props.level}
                handleEditTask={props.handleEditTask}
                handleChangePriority={props.handleChangePriority}
                handleDeleteTask={props.handleDeleteTask}
                handleCompleteTask={props.handleCompleteTask}
                selectedTask={task}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Layout;
