import React, { useState, useRef, useEffect } from "react";

const iconBtn = (
  { onClick, icon, color, ringColor, extraProps }
) => (
  <button
    onClick={onClick}
    className={`flex items-center justify-center w-10 h-10 rounded bg-gray-800 text-gray-300 hover:${color} hover:bg-gray-700 transition transform hover:scale-110 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-${ringColor}`}
    tabIndex={0}
    {...extraProps}
  >
    {icon}
  </button>
);

const priorities = [
  {
    value: "High",
    label: "High",
    icon: <span className="text-red-500 text-lg">ðŸ”¥</span>,
    color: "text-red-500"
  },
  {
    value: "Medium",
    label: "Medium",
    icon: <span className="text-yellow-400 text-lg">âš¡</span>,
    color: "text-yellow-400"
  },
  {
    value: "Low",
    label: "Low",
    icon: <span className="text-green-400 text-lg">ðŸŒ±</span>,
    color: "text-green-400"
  }
];

const Action = (props) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown on outside click
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    }
    if (showDropdown) {
      document.addEventListener("mousedown", handleClickOutside);
    } else {
      document.removeEventListener("mousedown", handleClickOutside);
    }
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [showDropdown]);

  return (
    <div className="flex gap-2 items-center relative">
      {iconBtn({
        onClick: () => {
          const edited = prompt("Edit task:", props.selectedTask.text);
          if (edited !== null && edited.trim() !== "") props.handleEditTask(edited);
        },
        icon: (
          <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M15.232 5.232l3.536 3.536M9 13l6.586-6.586a2 2 0 112.828 2.828L11.828 15.828a2 2 0 01-2.828 0L9 13zm0 0V21h8" /></svg>
        ),
        color: "text-blue-400",
        ringColor: "blue-500"
      })}
      {/* Change Priority Dropdown */}
      <div className="relative" ref={dropdownRef}>
        {iconBtn({
          onClick: () => setShowDropdown((v) => !v),
          icon: (
            <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M8 7V3m8 4V3m-9 4h10a2 2 0 012 2v10a2 2 0 01-2 2H7a2 2 0 01-2-2V9a2 2 0 012-2zm3 4h4m-2 0v4" /></svg>
          ),
          color: "text-yellow-400",
          ringColor: "yellow-500",
          extraProps: { "aria-haspopup": true, "aria-expanded": showDropdown }
        })}
        {showDropdown && (
          <div className="absolute right-0 mt-2 w-40 bg-gray-900 border border-gray-700 rounded-xl shadow-lg z-50 animate-fade-in flex flex-col py-2">
            {priorities.map((p) => (
              <button
                key={p.value}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-gray-800 transition text-left ${p.color} ${props.selectedTask.priority === p.value ? 'bg-gray-800' : ''}`}
                onClick={() => {
                  setShowDropdown(false);
                  props.handleChangePriority(p.value);
                }}
              >
                {p.icon}
                <span className="font-medium">{p.label}</span>
              </button>
            ))}
          </div>
        )}
      </div>
      {iconBtn({
        onClick: () => {
          if (window.confirm(`Are you sure you want to delete this ${props.priority} priority task?`)) {
            props.handleDeleteTask();
          }
        },
        icon: (
          <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" /></svg>
        ),
        color: "text-red-400",
        ringColor: "red-500"
      })}
    </div>
  );
};

export default Action;
